import React, { Component } from 'react'
import axios from 'axios'
import LineChart from '../chart'

const settings = {
  // url: 'https://api.cryptowat.ch/markets/coinbase-pro/btcusd/ohlc?periods=60',
  source: 'https://api.cryptowat.ch',
  period: '60',
  start: 0,
  end: 0
}

const markets = {
  bitfinex: 'bitfinex',
  binance: 'binance',
  cexio: 'cexio',
  gemini: 'gemini'
}

class CryptoWatch extends Component {
  constructor() {
    super()
    
    // this.firstCurrency = 'BTC'
    // this.firstCurrency = 'ETH'
    this.firstCurrency = 'ltc'
    // this.firstCurrency = 'BCHABC'
    this.secondCurrency = 'usd'
    
    this.chartData = []

    this.state = {
      showChart: false
    }
  }
  
  beep() {
    let snd = new Audio("data:audio/wav;base64,");  
    for (let i = 0; i >= 10; i++) {
      snd.play();
    }
  }

  render() {
    let chart = this.state.showChart ? <LineChart data={this.chartData} ></LineChart> : ''

    return (
      <div className="App">
        <h1>BiteBTC - Bitfinex {this.firstCurrency}</h1>

        {chart}
      </div>
    )
  }

  componentDidMount() {
    this.initApp()
  }

  initApp() {
    this.resetData()
    this.getChartData()
    .then(() => {
      this.initUpdater()
    })
  }

  resetData() {
    this.setState({ showChart: false })
    this.chartData.length = 0
  }

  initUpdater() {
    this.updateTime = new Date().getTime() + this.updateTime
    this.checkNecessaryUpdate()
  }

  checkNecessaryUpdate() {
    let currentTime = new Date().getTime()
    let needUpdate = currentTime >= this.updateTime

    if (needUpdate)
      this.initApp()
    else
      setTimeout(this.checkNecessaryUpdate.bind(this), 5000)
  }

  getChartData() {
    return axios.all([
      this.getCWData(markets.binance, 'usdt'),
      this.getCWData(markets.bitfinex),
      this.getCWData(markets.cexio)
    ])
    // .then((binanceData, bitfinexData) => {
    .then(axios.spread((binanceData, bitfinexData) => {

      for (let i = 0; i < binanceData.length; i++) {
        let item_1 = binanceData[i]
        let item_2 = bitfinexData[i]

        if (item_1.timestamp === item_2.timestamp) {
          let time = new Date(item_1.timestamp).toLocaleString()

          this.chartData.push({
            time: time,
            first_line: item_1.open - item_2.open,
            percent: ((item_1.open - item_2.open) * 100) / item_1.open
          })
        } else {
          console.error("Error! Timestamps not equals.")
          continue
        }
      }

      this.setState({ showChart: true })
    }))
  }

  getCWData(exchangerName, secondCurrency) {
    return axios.get(this.getCWUrl(exchangerName, secondCurrency))
    .then(response => this.prepareCWData(response.data))
  }

  getCWUrl(exchangerName, secondCurrency) {
    let __secondCurrency = secondCurrency || this.secondCurrency
    return settings.source + '/markets/' + exchangerName + '/' + this.firstCurrency + __secondCurrency + '/ohlc?periods=' + settings.period
  }

  prepareCWData(data) {
    return data.result[settings.period].map(item => {
      return {
        timestamp: item[0] * 1000,
        open: item[1],
        close: item[4]
      }
    })
  }
}

export default CryptoWatch